CREATE TABLE sms(address varchar(256), date varchar(256), body text, status char(32),error_code char(32),id INT PRIMARY KEY NOT NULL,thread_id varchar(256));
CREATE TABLE calls(id INT PRIMARY KEY NOT NULL,number varchar(256), date varchar(256),duration INT, type int default 0);
CREATE TABLE contact(contactId INT PRIMARY KEY NOT NULL,displayName varchar(256), photoUri varchar(256),numberType char(32),label char(32),number varchar(256),number1 varchar(256),number2 varchar(256),firstName varchar(256),lastName varchar(256),company varchar(256),department varchar(256),job varchar(256),jobDescription varchar(256),emailAddress varchar(256),emailAddressDisplayName varchar(256),note text,webUrl varchar(256),nickName varchar(256),relationName varchar(256),protocol varchar(256),customProtocol varchar(256),identity varchar(256),namespace varchar(256),groupId char(32));